import logo from './logo.svg';
import './App.css';
import React, { useState } from "react";
import axios from "axios";

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          R2D2 Project
        </p>
        <p>
          R2D2 Project
        </p>
      </header>
    </div>
  );
}

function App2() {
  const [messages, setMessages] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const input = e.target.elements.chatbotInput.value;
    if (!input) {
      return;
    }

    const response = await axios.post("https://api.openai.com/v1/engine/davinci-codex/completions", {
      prompt: input,
      max_tokens: 50,
      temperature: 0.7,
      n: 1,
      stop: "\n",
    }, {
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`,
      }
    });

    const message = {
      text: response.data.choices[0].text,
      isBot: true,
    };
    setMessages([...messages, message]);
    e.target.elements.chatbotInput.value = "";
  };

  return (
    <div id="chatbot-container">
      <div id="chatbot-messages">
        {messages.map((message, index) => (
          <div key={index} className={`message-container ${message.isBot ? "bot-message" : "user-message"}`}>
            <div className="message-text">{message.text}</div>
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit}>
        <input type="text" id="chatbot-input" placeholder="Ask me something..." />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default App;
