import React, { useState, useEffect } from "react";
import sendicon from '.././images/sendicon.svg';
import { GetDefaultMessages, GetPromptResponseMessage } from "./Utils";
import backgroundImage from ".././images/ChatBack.jpg";

function ChatInterface() {
    const [messageList, setMessageList] = useState([]);
    const [newMessage, setNewMessage] = useState('');

    const [defaultMessages, setDefaultMessages] = useState([]);

    function LoadStarterMessages() {
        for (let i = 0; i < defaultMessages.length; i++) {
            appendDefaultMessageToList(defaultMessages[i])
        }
    };


    function onChangeDynamicMessageInput(event) {
        setNewMessage(event.target.value);
    };

    function sendMessage() {
        if (!newMessage) {
            return;
        }
        appendNewMessageToList()
        appendDefaultMessageToList(GetPromptResponseMessage(newMessage))
        setNewMessage('');
    };

    function appendNewMessageToList() {
        setMessageList(messageList => {
            return [...messageList, { content: newMessage, sent: true }];
        });

    };

    function appendDefaultMessageToList(message) {
        setMessageList(messageList => {
            return [...messageList, { content: message.content, sent: message.sent }];
        });
    };

    useEffect(() => {
        async function getData() {
            const defaultMessages = await GetDefaultMessages();
            setMessageList(defaultMessages)
        }

        getData();
    }, [])

    useEffect(() => {
        // scroll to bottom when messages change
        containerRef.current.scrollTop = containerRef.current.scrollHeight;
      }, [messageList]);

    return (
        <div className="chatinterface">
            <div className="roomName">
                <h1>R2D2 Project</h1>
            </div>
            <div className="messages">
                {messageList.map((message, index) => (
                    <div
                        key={index}
                        className={`message ${message.sent ? 'sent' : 'received'}`}
                    >
                        {message.content}
                    </div>
                ))}
            </div>
            <div className="message-input">
                <input
                    type="text"
                    placeholder="Type your message..."
                    value={newMessage}
                    onChange={onChangeDynamicMessageInput}
                />
                <button onClick={sendMessage}>
                    <img src={sendicon} alt="Send" />
                </button>
            </div>
        </div>
    );
}

export default ChatInterface;
